    function init() {
        // Basic options for a simple Google Map
        var pin = 'images/pin.png';
        var ravensburg = new google.maps.LatLng(49.103816666667,8.865936111111);
        // For more options see: https://developers.google.com/maps/documentation/javascript/reference#MapOptions
        var mapOptions = {
            // How zoomed in you want the map to start at (always required)
            zoom: 11,
            mapTypeId: google.maps.MapTypeId.ROADMAP,
            // The latitude and longitude to center the map (always required)
            center: ravensburg,
            scrollwheel: false,
            zoomControl: true,
            // How you would like to style the map.
            // This is where you would paste any style found on Snazzy Maps.
            styles: mapStyle
        };
        // Get the HTML DOM element that will contain your map
        // We are using a div with id="map" seen below in the <body>
        var mapElement = document.getElementById('map');
        // Create the Google Map using our element and options defined above
        var map = new google.maps.Map(mapElement, mapOptions);
        // Let's also add a markers while we're at it
        var marker = new google.maps.Marker({
            position: ravensburg,
            map: map,
            title: 'Ravensburg',
            icon: pin,
            animation: google.maps.Animation.DROP
        });
        
        var clickedMarker = false;
        
        marker.addListener('click', function() {
            var handleError = function() {
                $('#goto-maps-location')[0].click();
                return true;                
            }
            
            if (!navigator.geolocation || clickedMarker) 
                return handleError();
            clickedMarker = true;
            
            navigator.geolocation.getCurrentPosition(function(position) {  
                
                try {
                    console.log("Latitude: " + position.coords.latitude + 
    "<br>Longitude: " + position.coords.longitude);	
                    var request = {
                        origin: new google.maps.LatLng(position.coords.latitude,
                            position.coords.longitude),
                        destination: ravensburg,
                        travelMode: google.maps.TravelMode.DRIVING
                    };
                    
                    var directionsService = new google.maps.DirectionsService();
                    var directionsDisplay = new google.maps.DirectionsRenderer();
                    directionsService.route(request, function (result, status) {
                        if (status == google.maps.DirectionsStatus.OK) {
                            directionsDisplay.setMap(map);
                            directionsDisplay.setDirections(result);
                        }
                    });
                } catch (error) {
                    console.error(error);
                    return handleError();
                }
            }, function(error) {
                return handleError();
            });
        });
    }

